import React from 'react';

const RepoSection = ({ reposData }) => {
  return (
    <div className='section section-repo'>
      <div className='section-repo__heading-box'>
        <h2 className='heading-secondary'>Github 專案</h2>
      </div>
      <div className='section-repo__repos-box'>
        {reposData.map((repo) => (
          <div className='repo__wrapper'>
            <div className='repo__title'>{repo.name}</div>
            <div className='repo__circle-box'>
              <p>{repo.description}</p>
            </div>
            <div className='repo__star-box'>
              <div className='repo__star-icon'></div>
              <div className='repo__star-number'>{repo.stargazers_count}</div>
            </div>
            <div className='repo__fork-box'>
              <div className='repo__fork-icon'></div>
              <div className='repo__fork-number'>{repo.forks}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RepoSection;
